sources.parcel/layout/_header.scss:
#splash h1,
#splash h2,
#splash p {
  color: white;
}

#splash h2 {
  font-size: 1.5rem;
  margin-bottom: 5vw;
}

#splash.container {
  // height: 60vh;
  display: grid;
  grid-template-columns: 1fr 20% 1fr;
  background-color: black;
  padding: 0;
  margin-bottom: 10vh;
}

#splash img {
  width: 50vw;
  max-width: 350px;
}

#splash.container::before {
  content: "";
  grid-row: 1;
  grid-column-start: 2;
  background: black;
  clip-path: polygon(0 0, 0% 100%, 100% 0);
  margin-left: -1px;
}

#splash .content {
  grid-column: 1/3;
  grid-row: 1;
  z-index: 1;
  display: flex;
  flex-direction: column;
  margin: 3vw 5vw;
}

#splash .header_image {
  grid-row: 1;
  grid-column: 2/4;
  background-image: url("assets/header_image.webp");
  background-size: cover;
  height: 100%;
  width: 100%;
  background-position: center;
}

#splash .column_top {
  top: 1vh;
}

#splash .column_bottom {
  margin: auto 0;
  max-width: 50%;
}

// @media only screen and (max-width: 580px) {
//   #splash.container {
//     height: 80vh;
//   }
// }

@media only screen and (max-width: 690px) {
  #splash img {
    max-width: 250px;
  }

  // #splash.container::before {

  //   clip-path: polygon(0 0, 0% 100%, 100% 0);
  //   margin-left: -1px;
  // }

  #splash.container::before {
    clip-path: polygon(0);
  }

  #splash .header_image {
    clip-path: polygon(100% 0, 0% 100%, 100% 100%);
  }
  #splash.container {
    grid-template-columns: 1fr 20% 2fr;
  }

  #splash .column_bottom {
    margin: auto 0;
    max-width: 100%;
  }
}

sources.parcel/layout/_indexform.scss:
@use './parcel/base/variables' as vars;

#contact input[type="text"] {
  appearance: none;
  -webkit-appearance: none;
}
#contact input[type="text"] {
  padding: 16px;
  background-color: vars.$black;
  color: vars.$white;
  font-family: vars.$font-regular;
  border: 2px solid vars.$purple;
  display: block;
  border-radius: 0;
  margin: 20px 0;
  width: 100%;
  outline: none;
}

#contact input[type="text"]:focus {
  outline: 2px solid rgb(165, 91, 187);
  border-radius: 0;
}

#contact input[type="email"] {
  padding: 16px;
  background-color: vars.$black;
  color: vars.$off-white;
  font-family: vars.$font-regular;
  border: 2px solid vars.$purple;
  display: block;
  margin: 20px 0;
  width: 100%;
  outline: none;
  transition: border 0.3s;
}

#contact input[type="email"]:focus {
  border: 2px solid vars.$purple;
  border-radius: 0;
  outline: 2px solid rgb(165, 91, 187);
  transition: border 0.3s;
}

#contact.container {
  background-color: vars.$black;
  margin: 0;
  height: auto;
}

#contact form {
  max-width: 800px;
  margin: 0 auto;
  padding: 10px 20px 120px 20px;
}
#contact h3 {
  color: vars.$off-white;
  font-family: vars.$font-bold;
  text-align: center;
  font-size: 2rem;
  padding-top: 50px;
}

#contact #next {
  outline: none;
  padding: 16px 50px;
  background-color: vars.$purple;
  color: vars.$white;
  font-size: 1rem;
  max-width: 300px;
  font-family: vars.$font-bold;
  border: 2px solid vars.$white;
  margin: 16px 0;
  text-align: center;
  transition: background-color 0.3s;
  border: 2px solid vars.$purple;
  margin-top: 10px;
  float: right;
}

#contact #next:hover {
  transition: 0.3s;
  color: vars.$white;
  background-color: vars.$black;
  border: 2px solid vars.$purple;
}

